function getAngle(x1, x2, y1, y2) {
	var angle;

	if (x1 == x2) {
		if (y1 < y2) { return 0; }
		else { return 180 * (Math.PI/180); }
	}
	else {
		angle = Math.atan((y2-y1)/(x2-x1));
		if (x1 < x2) {
			return angle - 90 * (Math.PI/180);
		}
		else {
			return angle + 90 * (Math.PI/180);
		}
	}
}
